module.exports = function (l, k) {
  var list = l || [];
  var key = k || 'id';

  if (!Array.isArray(list)) {
    throw new TypeError('`list` must be of type Array');
  }

  return list.reduce(function (accum, item) {
    if (item && item[key] != null) {
      accum[item[key]] = item;
    }

    return accum;
  }, {});
};
